import { Link } from "react-router-dom";
import { useEffect } from "react";

import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import classes from "./AvailableMeals.module.css";
import cheezyTikka from "../../assets/Cheezy-Tikka.jpg";
import cheeseSticks from "../../assets/Cheese-Sticks.jpg";
import ovenBakedWings from "../../assets/Oven-Baked-Wings.jpg";
import mexicanSandwich from "../../assets/Mexican-Sandwich.jpg";

const DUMMY_MEALS = [
  {
    id: "m1",
    name: "Cheezy Tikka",
    description: "A Cheezy Fusion! A Cheese Sauce base with Tikka topping.",
    price: 1260,
    image: cheezyTikka,
  },
  {
    id: "m2",
    name: "Cheese Sticks",
    description: "Freshly Baked Bread Filled with the Yummiest Cheese Blend",
    price: 560,
    image: cheeseSticks,
  },
  {
    id: "m3",
    name: "Oven Baked Wings",
    description: "Fresh Oven Baked Wings Tossed In Hot Peri Peri Sauce",
    price: 370,
    image: ovenBakedWings,
  },
  {
    id: "m4",
    name: "Mexican Sandwich",
    description: "Mozzarella Dipped Chicken Topped with Garlic Sauce",
    price: 700,
    image: mexicanSandwich,
  },
];

const AvailableMeals = () => {
  const [mealData, setMealData] = [];

  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch(
        "https://food-order-app-1974f-default-rtdb.firebaseio.com/meals.json"
      );
      const data = await response.json();

      const loadedMeals = [];

      for (const key in data) {
        loadedMeals.push({
          id: key,
          name: data[key].name,
          description: data[key].description,
          price: data[key].price,
        });
      }

      setMealData(loadedMeals);
    };

    fetchMeals();
  }, []);

  const mealsList = mealData.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
      <Link to="/items" className={classes.link}>
        <div className={classes["small-card"]}>+ Show all items</div>
      </Link>
    </section>
  );
};

export default AvailableMeals;
